module.exports={"Defs":{"DifficultyDef":[{"defName":"VeryEasy","label":"Base builder","description":"For those who want to relax and build a colony in a sandbox-like environment. Threats appear, but they're extremely weak. Some of the more dangerous events are disabled.","difficulty":1,"threatScale":0.05,"colonistMoodOffset":5,"baseSellPriceFactor":0.7,"cropYieldFactor":1.3,"diseaseIntervalFactor":3,"enemyReproductionRateFactor":0.1},{"defName":"Easy","label":"Some challenge","description":"For players on their first game, or casual players who still want a challenge. Threats are dangerous but not brutal.","difficulty":2,"threatScale":0.35,"colonistMoodOffset":-5,"baseSellPriceFactor":0.6,"cropYieldFactor":1.2,"diseaseIntervalFactor":1.5,"enemyReproductionRateFactor":0.4},{"defName":"Medium","label":"Rough","description":"It's a rough planet. Threats can be quite dangerous. Not recommended until you've got some experience with the game.","difficulty":3,"threatScale":0.65,"colonistMoodOffset":-10,"baseSellPriceFactor":0.55,"cropYieldFactor":1,"diseaseIntervalFactor":1,"enemyReproductionRateFactor":1},{"defName":"Hard","label":"Intense","description":"A significant challenge. Succeeding at this level requires a solid and well-rounded understanding of survival strategies, and some luck.","difficulty":4,"threatScale":1,"colonistMoodOffset":-13,"baseSellPriceFactor":0.55,"cropYieldFactor":1,"diseaseIntervalFactor":1,"enemyReproductionRateFactor":1},{"defName":"VeryHard","label":"Extreme","description":"As hard as a diamond-bladed dagger. Enemies are strong, prices are poor, food is scarce, and colonists are grumpy. For those who enjoy digital suffering.","difficulty":5,"threatScale":1.3,"colonistMoodOffset":-18,"baseSellPriceFactor":0.47,"cropYieldFactor":0.9,"diseaseIntervalFactor":0.9,"enemyReproductionRateFactor":1}]}}