module.exports={"MapConditionDefs":{"MapConditionDef":[{"defName":"SolarFlare","label":"solar flare","description":"A solar flare is hitting the planet. This creates extreme electrical interference and prevents most electrical devices from working.","endMessage":"The solar flare is ending.","canBePermanent":true},{"defName":"Eclipse","conditionClass":"MapCondition_Eclipse","label":"eclipse","description":"An eclipse is shadowing the surface. It will be dark, even during the daytime.","endMessage":"The eclipse is ending.","canBePermanent":true},{"defName":"PsychicDrone","conditionClass":"MapCondition_PsychicEmanation","label":"psychic drone","description":"A distant, powerful AI is emitting waves of psychic force that are driving people towards insanity.","droneLevel":"BadMedium","endMessage":"The psychic drone is ending.","exclusiveConditions":{"li":"PsychicSoothe"},"canBePermanent":true},{"defName":"PsychicSoothe","conditionClass":"MapCondition_PsychicEmanation","label":"psychic soothe","description":"A distant, powerful AI is emitting waves of psychic force that are calming people and stabilizing their minds.","droneLevel":"GoodMedium","endMessage":"The psychic soothe is ending.","exclusiveConditions":{"li":"PsychicDrone"},"canBePermanent":true},{"defName":"ToxicFallout","conditionClass":"MapCondition_ToxicFallout","label":"toxic fallout","description":"A plume of toxic dust from some distant source is slowly settling over this entire region of the planet. Anyone not under a roof will be slowly poisoned.","endMessage":"The worst of the toxic fallout has settled.","canBePermanent":true},{"defName":"VolcanicWinter","conditionClass":"MapCondition_VolcanicWinter","label":"volcanic winter","description":"The atmosphere is choked with volcanic particles, significantly reducing the amount of light which reaches the surface. Temperatures will be colder and plants will grow slower.","endMessage":"Most of the volcanic ash has settled.","canBePermanent":true},{"defName":"HeatWave","conditionClass":"MapCondition_HeatWave","label":"heat wave","description":"An extreme wave of heat is baking the area.","endMessage":"The heat wave is over.","exclusiveConditions":{"li":["ColdSnap","VolcanicWinter"]}},{"defName":"ColdSnap","conditionClass":"MapCondition_ColdSnap","label":"cold snap","description":"An unusual cold snap is freezing the region.","endMessage":"The cold snap is over.","exclusiveConditions":{"li":"HeatWave"}},{"defName":"Flashstorm","conditionClass":"MapCondition_Flashstorm","label":"flashstorm","description":"Extreme amounts of lightning are hitting a small region.","endMessage":"The flashstorm is over.","preventRain":true,"canBePermanent":true},{"defName":"ClimateCycle","conditionClass":"MapCondition_ClimateCycle","label":"climate cycle","description":"A multi-year climate cycle due to an elliptical orbit.","canBePermanent":true},{"defName":"Planetkiller","conditionClass":"MapCondition_Planetkiller","label":"planetkiller","description":"An interstellar planetkiller weapon is approaching this planet. When it hits, it will destroy all life."}]}}