module.exports={"ResearchProjectDefs":{"ResearchProjectDef":[{"defName":"PsychiteRefining","label":"psychite refining","description":"Learn to refine psychoid leaves into flake and yayo, different forms of the euphoric drug psychite.","baseCost":400,"techLevel":"Industrial","prerequisites":{"li":"DrugProduction"}},{"defName":"WakeUpProduction","label":"wake-up production","description":"Your chemists learn the formulas and techniques for synthesizing wake-up, a work performance-enhancing drug.","baseCost":600,"techLevel":"Industrial","prerequisites":{"li":"DrugProduction"}},{"defName":"GoJuiceProduction","label":"go-juice production","description":"Learn to produce the synthetic combat-performance-enhancing drug go-juice.","baseCost":1000,"techLevel":"Industrial","prerequisites":{"li":"DrugProduction"}},{"defName":"MalariBlockProduction","label":"malari-block production","description":"Learn to produce malari-block, a malaria prevention drug.","baseCost":500,"techLevel":"Industrial","prerequisites":{"li":"DrugProduction"}},{"defName":"Batteries","label":"batteries","description":"Build batteries for storing electricity.","baseCost":400,"techLevel":"Industrial","prerequisites":{"li":"Electricity"},"tags":{"li":"ClassicStart"}},{"defName":"NutrientPaste","label":"nutrient paste","description":"Build nutrient paste dispensers which efficiently produce edible meals from raw nutritive feedstocks.","baseCost":600,"techLevel":"Industrial","prerequisites":{"li":"Electricity"},"tags":{"li":"ClassicStart"}},{"defName":"SolarPanels","label":"solar panels","description":"Build solar panels for electrical generation.","baseCost":600,"techLevel":"Industrial","prerequisites":{"li":"Electricity"},"tags":{"li":"ClassicStart"}},{"defName":"AirConditioning","label":"air conditioning","description":"Build air conditions to cool rooms in the heat, or to keep freezers.","baseCost":600,"techLevel":"Industrial","prerequisites":{"li":"Electricity"},"tags":{"li":"ClassicStart"}},{"defName":"Autodoors","label":"autodoors","description":"Build autodoors which open to permit movement without slowing anyone down.","baseCost":400,"techLevel":"Industrial","prerequisites":{"li":"Electricity"}},{"defName":"Hydroponics","label":"hydroponics","description":"Allows you to build hydroponics basins to rapidly grow crops indoors regardless of the terrain or weather outside.","baseCost":700,"techLevel":"Industrial","prerequisites":{"li":"Electricity"}},{"defName":"ElectricSmelting","label":"electric smelting","description":"Build electric smithy and smelters.\\n\\nThe electric smithy allows smithing items without fuel. The electric smelters uses heat and strong electromagnets to extract useful metal from slag chunks and other items.","baseCost":1700,"techLevel":"Industrial","prerequisites":{"li":"Electricity"}},{"defName":"ElectricCremation","label":"electric cremation","description":"Build crematoriums which can vaporize unwanted corpses.","baseCost":500,"techLevel":"Industrial","prerequisites":{"li":"ElectricSmelting"}},{"defName":"ColoredLights","label":"colored lights","description":"Allows colonists to construct colored lights for decorative purposes.","baseCost":500,"techLevel":"Industrial","prerequisites":{"li":"Electricity"}},{"defName":"Machining","label":"machining","description":"Build machining tables to craft guns and machinery or disassemble dead mechanoids.","baseCost":1500,"techLevel":"Industrial","prerequisites":{"li":["Electricity","Smithing"]}},{"defName":"Mortars","label":"mortars","description":"Learn to build mortars which can lob shells long distances over walls.","baseCost":2000,"techLevel":"Industrial","prerequisites":{"li":["Electricity","Machining"]}},{"defName":"IEDBomb","label":"Explosive IEDs","description":"Allows colonists to build improvised explosive device-based traps from artillery shells.","baseCost":1600,"techLevel":"Industrial","prerequisites":{"li":"Electricity"}},{"defName":"IEDIncendiary","label":"Incendiary IEDs","description":"Allows colonists to build incendiary explosive traps from artillery shells.","baseCost":1600,"techLevel":"Industrial","prerequisites":{"li":"Electricity"}},{"defName":"MicroelectronicsBasics","label":"microelectronics basics","description":"The basics of working with delicate, complex electronics. This unlocks the high-tech research bench, comms console, and other research projects like tube televisions, and cryptosleep caskets.","baseCost":700,"techLevel":"Industrial","prerequisites":{"li":"Electricity"}},{"defName":"TubeTelevision","label":"tube television","description":"Allows the production of tube televisions.","baseCost":800,"techLevel":"Industrial","prerequisites":{"li":"MicroelectronicsBasics"}},{"defName":"GunTurrets","label":"gun turrets","description":"Allows the production of automated gun turrets.","baseCost":800,"techLevel":"Industrial","prerequisites":{"li":"MicroelectronicsBasics"}}]}}